import React, { useState } from "react";
import Slider from "./components/Slider";
import MenuButton from "./components/MenuButton";

const API_KEY = "563492ad6f91700001000001e2f01e5688a94b13b884103cacf2626b";
const endpoint =
  "https://api.pexels.com/v1/search/?page=1&per_page=40&query=Nature";

const App = () => {
  const [altText, setAltText] = useState("");
  const [photographer, setPhotographer] = useState("");
  const [id, setId] = useState(null);
  const [height, setHeight] = useState(null);
  const [width, setWidth] = useState(null);
  const [photoURL, setPhotoURL] = useState("");
  const [photoLeft, setPhotoLeft] = useState("");
  const [photoRight, setPhotoRight] = useState("");
  const [isUpdated, setIsUpdated] = useState(false);
  const [showSlider, setShowSlider] = useState(false);
  const [photosList, setPhotosList] = useState([]);
  const [index, setIndex] = useState(null);

  const getPhotos = async (search, handler) => {
    await fetch(search, {
      headers: {
        Authorization: API_KEY,
      },
    })
      .then(
        (res) => res.json(),
        (rej) => console.log(rej)
      )
      .then((data) => {
        if (!isUpdated) {
          setIsUpdated(true);
          handler(data);
        }
      });
  };

  const displayRandomPhoto = (data) => {
    console.log("[DATA] ", data);
    setPhotosList(data.photos);
    setIndex(Math.floor(Math.random() * photosList.length));
    if (index === 0) index += 1;
    if (index === photosList.length - 1) index -= 1;
    let photoMid = photosList[index];

    window.innerWidth >= 800
      ? (document.body.style.backgroundImage = `url('${photoMid.src.landscape}')`)
      : (document.body.style.backgroundImage = `url('${photoMid.src.portrait}')`);

    setPhotoLeft(photosList[index - 1].src.tiny);
    setPhotoRight(photosList[index + 1].src.tiny);
    setAltText(photoMid.alt);
    setPhotographer(photoMid.photographer);
    setPhotoURL(photoMid.src.tiny);
    setId(photoMid.id);
    setHeight(photoMid.height);
    setWidth(photoMid.width);
  };

  const updateSlider = () => {
    return (
      <Slider
        altText={altText}
        photographer={photographer}
        src={photoURL}
        id={id}
        height={height}
        width={width}
        photoLeft={photoLeft}
        photoRight={photoRight}
      />
    );
  };

  getPhotos(endpoint, displayRandomPhoto);

  return (
    <>
      <button className='btn-left'>Left</button>
      {showSlider && (
        <Slider
          altText={altText}
          photographer={photographer}
          src={photoURL}
          id={id}
          height={height}
          width={width}
          photoLeft={photoLeft}
          photoRight={photoRight}
        />
      )}
      <button className='btn-right'>Right</button>

      <div className='flex-column-container'>
        <MenuButton
          onClick={() => setShowSlider(!showSlider)}
          menuButtonText={showSlider ? "v Menu" : "^ Menu"}
        ></MenuButton>
      </div>
    </>
  );
};

export default App;
